### int removeDups (LInt *)
```
int removeDups (LInt *l){
	LInt ll = *l;
	int count = 0;
	if (ll==NULL)
		return 0;

	LInt aux = newLInt(ll->valor, NULL);
	LInt head_of_aux = aux;
	LInt ante = ll;
	ll = ll->prox;

	while (ll){
		while(aux){
			if (ll->valor == aux->valor){
				aux = head_of_aux;
				count ++;
				break;
			}
			aux = aux->prox;
		}

		if (aux==NULL){
			aux = newLInt(ll->valor,head_of_aux);
			head_of_aux = aux;
			ante = ll;
			ll = ll->prox;
		}
		else{
		aux = head_of_aux;
		ante->prox = ll->prox;
		free(ll);
		ll = ante->prox;
		}
	}
	return count;
}
