	ll = ll->prox;
	while (ll){
		if (ll->valor > biggest){
			biggest = ll->valor;
		}
		ll = ll->prox;
	}

	ll = aux ->prox;
	while (ll){
		if (ll->valor == biggest){
			ante->prox = ll->prox;
			free (ll);
			break;
		}
		else{
			ante = ll;
			ll = ll->prox;
		}
	}

	*l = aux;
	return biggest;
}
