### int take (int, LInt *)
```
int take (int n, LInt *l){
	int size = 1;
	LInt ante;
	LInt ll = *l;
	LInt end;
	while (ll && size < n){
		ll = ll->prox;
		size ++;
	}

	if (ll==NULL){
		size--;
		return size;
		}

	end = ll;
	ll = ll->prox;
	end->prox=NULL;
	if (ll==NULL)
		return size;

	ante = ll;
	ll =ll->prox;
	if (ll == NULL){
		free(ante);
		return size;
	}
	while (ll->prox) {
		free(ante);
		ante = ll;
		ll = ll->prox;
	}
	//free(ante);
	return size;
}
