### int drop (int, LInt *)
```
int drop (int n, LInt *l){
	LInt ll = *l;
	int count = 0;
	if (ll == NULL || n == 0)
		return count;

	count++;
	LInt ante = ll;
	ll=ll->prox;
	while (count!=n && ll){
		free (ante);
		ante = ll;
		ll = ll->prox;
		count++;
	}
	*l =ll;
	return count;
}
