### void remreps (LInt)
```
void remreps (LInt l){
	if (l==NULL || l->prox==NULL)
		return;
	LInt head = l;
	int chosen = head->valor;
	l = l->prox;
	LInt ante = l;
	l=l->prox;
	while (l){
		if (l->valor == chosen && l->prox){
			ante->prox = l->prox;
			free(l);
			l=ante->prox;
		}
		else if (l->valor == chosen && l->prox==NULL){
			ante->prox = NULL;
			free (l);
		}
		ante = ante->prox;
		l=l->prox;
		head = head->prox;
		chosen = head->valor;
	}
}
