### LInt fromArray (int v[], int)
```
LInt fromArray (int v[], int N){
	if (N==0)
		return NULL;

	int i;
	LInt result = newLInt(v[0],NULL);
	LInt head_of_res = result;

	for (i=1; i<N; i++){
		result->prox = newLInt(v[i],NULL);
		result = result->prox;
	}
	return head_of_res;
}
