LInt rotateL (LInt l){
	if (l==NULL || l->prox == NULL)
		return l;
	LInt head = l;
	LInt aux = l->prox;

	while (l->prox){
		l=l->prox;
	}
	l->prox = head;
	head->prox = NULL;
	return aux;
}
